sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
    var ass_id = inputs.assessment_id;
    gs.info("Assessment Id : " + ass_id);

    try { 
        var r = new sn_ws.RESTMessageV2('x_1507800_complian.Start assessment', 'Default POST');

        //override authentication profile 
        //authentication type ='basic'/ 'oauth2'
        r.setAuthenticationProfile('oauth2', 'Krit CC App Reg 5 default_profile');

        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();

        outputs.responseBody = responseBody;
        outputs.httpStatus = httpStatus;
    }
    catch(ex) {
        var message = ex.message;
    }
    

})(inputs, outputs);

