sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

	// Add your code here
	var gr = new GlideRecord("sn_compliance_policy_statement");
    gr.addQuery('name', 'STARTSWITH', source.u_main);
	gr.query();
	if (gr.next()){
		var rec_id = gr.sys_id;
		target.parent = rec_id;
	}
	if (source.u_has_downstream_controls == "TRUE" || source.u_has_downstream_controls == "True" || 
				source.u_has_downstream_controls == "true" )
		target.has_downstream_controls = true;

	if (source.u_create_control_requirements == "TRUE" || source.u_create_control_requirements == "True" || 
				source.u_create_control_requirements == "true" )
		target.create_control_requirements = true;

	if (source.u_creates_cont__automatically == "TRUE" || source.u_creates_cont__automatically == "True" || 
				source.u_creates_cont__automatically == "true" )
		target.auto_control_create = true;

	if (source.u_active == "TRUE" || source.u_active == "True" || source.u_active == "true" )
		target.active = true;

    gr = null;

})(source, target, map, log, action==="update");
