sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

	// Add your code here
	gr_entitytype = new GlideRecord('sn_grc_profile_type');
	gr_entitytype.get('name', source.u_entity_type);
	gr_entitytype.query();
	if (gr_entitytype.next())
		target.profile_type = gr_entitytype.sys_id;

	var gr_class = new GlideRecord('sn_grc_profile_class');
	gr_class.get('name', source.u_entity_class);
	gr_class.query();
	if (gr_class.next())
		target.profile_class = gr_class.sys_id;

	var gr_owner = new GlideRecord('sys_user');
	gr_owner.get('name', source.u_user);
	gr_owner.query();
	if (gr_owner.next())
		target.default_owner = gr_owner.sys_id;

	target.condition = source.u_condition;
	target.entity_filter_type = 'Build your own conditions';
	target.use_owner_field = false;
	target.table = source.u_table;

	gr_entitytype = null;
	gr_class = null;
	gr_owner = null;

})(source, target, map, log, action==="update");
