sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

	// Add your code here
	var gr_controlobj = new GlideRecord("sn_compliance_policy_statement");
	gr_controlobj.addQuery('name', source.u_name);
	gr_controlobj.query();
	if (gr_controlobj.next())
		target.sn_compliance_statement = gr_controlobj.sys_id;
	gr_controlobj = null;

	var gr_entitytype = new GlideRecord("sn_grc_profile_type");
	gr_entitytype.addQuery('name', source.u_entity_type);
	gr_entitytype.query();
	if (gr_entitytype.next())
		target.sn_grc_profile_type = gr_entitytype.sys_id;
	gr_entitytype = null;

	var gr_contentobj = new GlideRecord("sn_grc_content");
	gr_contentobj.addQuery('name', source.u_name);
	gr_contentobj.query();
	if (gr_contentobj.next())
		target.sn_grc_content = gr_contentobj.sys_id;
	gr_contentobj = null;
})(source, target, map, log, action==="update");
